// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.10;

import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";
import { ILiquidator } from "../interfaces/ILiquidator.sol";
import { ITransferBridge } from "../interfaces/ITransferBridge.sol";
import { IERC20Metadata } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

/// @title CrossChainLiquidator.sol
/// @notice Bridges tokens to another Liquidator on another chain
contract CrossChainLiquidator is ILiquidator, AccessControl {
  ITransferBridge public transferBridge; // Address of the TransferBridge contract
  address public hubLiquidator; // Address of the liquidator in the hub chain
  uint32 public hubChainId;

  bytes32 public constant LIQUIDATOR_ADMIN_ROLE = keccak256("LIQUIDATOR_ADMIN_ROLE");

  constructor(ITransferBridge _transferBridge, address _hubLiquidator, uint32 _hubChainId, address admin) {
    require(address(_transferBridge) != address(0), "Invalid transfer bridge address");
    require(_hubLiquidator != address(0), "Invalid hub liquidator address");

    transferBridge = _transferBridge;
    hubLiquidator = _hubLiquidator;
    hubChainId = _hubChainId;
    _grantRole(DEFAULT_ADMIN_ROLE, admin);
  }

  function liquidate(
    address asset,
    uint256 assetAmount,
    uint256 debtAmount
  ) external override onlyRole(LIQUIDATOR_ADMIN_ROLE) {
    transferBridge.transferTokenAndData(
      IERC20Metadata(asset),
      hubChainId,
      hubLiquidator,
      assetAmount,
      abi.encode(debtAmount) // TODO: Check the data
    );
  }
}

contracts/liquidators/EOALiquidator.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.10;

import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { ILiquidator } from "../interfaces/ILiquidator.sol";

/// @title EOALiquidator.sol
/// @notice Sends the tokens to another address (an EOA) and we prey that EOA isn't corrupt and behaves well and
///         liquidates the token sending the results to the RiskHub.
///         While we learn how to use CowProtocol programatic orders, the idea is sending funds to an EOA, that will
///         be listening TokensToLiquidate and issuing CowProtocol intents. After the swap is executed, the resulting
///         funds will be transferred to the main chain.
///         This can also be used for Fusion+ cross-chain intents, that AFAIK can't be created from smart contracts.
///         Cross-chain intents will be a perfect fit for our protocol, we avoid the additional bridge cost.
contract EOALiquidator is ILiquidator, AccessControl {
  using SafeERC20 for IERC20;

  bytes32 public constant EOA_ADMIN_ROLE = keccak256("EOA_ADMIN_ROLE");

  address public eoa;

  event EOAChanged(address indexed newEOA, address indexed oldEOA);
  event TokensToLiquidate(address indexed eoa, address indexed asset, uint256 assetAmount, uint256 debtAmount);

  /// @param eoa_ Address of the EOA that will perform the liquidation
  /// @param admin Address of the administrator of the contract
  constructor(address eoa_, address admin) {
    require(eoa_ != address(0), "Must define an EOA");
    eoa = eoa_;
    emit EOAChanged(eoa_, address(0));
    _grantRole(DEFAULT_ADMIN_ROLE, admin);
  }

  function setEOA(address newEOA) external onlyRole(EOA_ADMIN_ROLE) {
    emit EOAChanged(newEOA, eoa);
    eoa = newEOA;
  }

  /// @inheritdoc ILiquidator
  /// @notice Sells the received `asset` for USDC via Uniswap and sends the USDC to RiskHub.
  /// @param asset Address of the token to liquidate
  /// @param assetAmount Amount of the token to liquidate
  /// @param debtAmount Minimum USDC required to cover the debt
  function liquidate(address asset, uint256 assetAmount, uint256 debtAmount) external override {
    IERC20(asset).safeTransfer(eoa, assetAmount);
    emit TokensToLiquidate(eoa, asset, assetAmount, debtAmount);
  }
}

contracts/liquidators/SwapLiquidator.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.10;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {ILiquidator} from "../interfaces/ILiquidator.sol";
import {ISwapRouter} from "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";

/// @title SwapLiquidator.sol
/// @notice Liquidates tokens into USDC using Uniswap on Arbitrum.
contract SwapLiquidator is ILiquidator, AccessControl {
    using SafeERC20 for IERC20;

    ISwapRouter public immutable swapRouter; // Uniswap V3 Router address
    address public immutable usdcToken;      // Address of USDC token
    address public immutable riskHub;        // Address of RiskHub contract

    uint24 public constant poolFee = 3000;   // Uniswap pool fee (0.3%)

    bytes32 public constant LIQUIDATOR_ADMIN_ROLE = keccak256("LIQUIDATOR_ADMIN_ROLE");

    /// @param _swapRouter Address of the Uniswap V3 Router
    /// @param _usdcToken Address of the USDC token on Arbitrum
    /// @param _riskHub Address of the RiskHub contract
    constructor(address _swapRouter, address _usdcToken, address _riskHub) {
        require(_swapRouter != address(0), "Invalid router address");
        require(_usdcToken != address(0), "Invalid USDC address");
        require(_riskHub != address(0), "Invalid RiskHub address");

        swapRouter = ISwapRouter(_swapRouter);
        usdcToken = _usdcToken;
        riskHub = _riskHub;
    }

    /// @inheritdoc ILiquidator
    /// @notice Sells the received `asset` for USDC via Uniswap and sends the USDC to RiskHub.
    /// @param asset Address of the token to liquidate
    /// @param assetAmount Amount of the token to liquidate
    /// @param debtAmount Minimum USDC required to cover the debt
    function liquidate (address asset, uint256 assetAmount, uint256 debtAmount) onlyRole(LIQUIDATOR_ADMIN_ROLE) external override {
        require(asset != address(0), "Invalid asset address");
        require(assetAmount > 0, "Asset amount must be greater than zero");

        // Approve the Uniswap router to spend the asset
        IERC20(asset).approve(address(swapRouter), assetAmount);

        // Define the swap parameters
        ISwapRouter.ExactInputSingleParams memory params = ISwapRouter.ExactInputSingleParams({
            tokenIn: asset,
            tokenOut: usdcToken,
            fee: poolFee,
            recipient: address(this), // USDC stays in the contract temporarily
            deadline: block.timestamp,
            amountIn: assetAmount,
            amountOutMinimum: debtAmount,
            sqrtPriceLimitX96: 0 // No price limit
        });

        // Execute the swap on Uniswap
        uint256 usdcReceived = swapRouter.exactInputSingle(params);
        require(usdcReceived >= debtAmount, "Insufficient USDC received");

        // Transfer the USDC to the RiskHub
        IERC20(usdcToken).safeTransfer(riskHub, usdcReceived);
    }
}

