// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.10;

import "../interfaces/IPriceOracle.sol";
import "@openzeppelin/contracts/access/Ownable2Step.sol";
import "@pythnetwork/pyth-sdk-solidity/IPyth.sol";
import "@pythnetwork/pyth-sdk-solidity/PythStructs.sol";
import { IERC20Metadata } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

/**
 * This contract assumes that the tokenOut is a token that
 * it's pegged with the USD dollar (1 tokenOut = 1 dollar)
 */
contract PythPriceOracle is IPriceOracle, Ownable2Step {
  struct PythPriceFeed {
    bytes32 id;
    uint256 age;
  }

  IPyth public pyth;
  mapping(IERC20Metadata tokenIn => mapping(IERC20Metadata tokenOut => PythPriceFeed feed)) public tokenPairPriceFeed;
  mapping(IERC20Metadata tokenIn => mapping(IERC20Metadata tokenOut => int256 price)) public tokenPairPrice;

  constructor(address owner, address pythContract) Ownable(owner) {
    pyth = IPyth(pythContract);
  }

  // define an event, to make a list on withdrawing
  event Withdraw(address indexed to, uint256 amount);

  // define a event to make a list of adding price source
  event FeedAdded(address indexed tokenIn, address indexed tokenOut, bytes32 indexed feedId);

  function getCurrentPrice(IERC20Metadata tokenIn, IERC20Metadata tokenOut) public view returns (uint256) {
    uint256 price = uint256(tokenPairPrice[tokenIn][tokenOut]);
    if (price == 0) {
      // TODO: Throw error
      return 0;
    }

    // TODO: We should check when was this updated
    return price;
  }

  // TODO: Check what happens if anyone can update this function
  function updatePrice(bytes[] calldata priceUpdate, IERC20Metadata tokenIn, IERC20Metadata tokenOut) external payable {
    PythPriceFeed memory feed = tokenPairPriceFeed[tokenIn][tokenOut];
    // TODO: Add custom errors
    // TODO: Check if the price corresponds to the feed
    //    require(feed.id == bytes32(priceUpdate[0]), "Wrong feed");
    // Submit a priceUpdate to the Pyth contract to update the on-chain price.
    // Updating the price requires paying the fee returned by getUpdateFee.
    // WARNING: These lines are required to ensure the getPriceNoOlderThan call below succeeds.
    // If you remove them, transactions may fail with "0x19abf40e" error.
    uint fee = pyth.getUpdateFee(priceUpdate);
    pyth.updatePriceFeeds{ value: fee }(priceUpdate);

    // Read the current price from a price feed if it is less than 60 seconds old.
    // Each price feed (e.g., ETH/USD) is identified by a price feed ID.
    // The complete list of feed IDs is available at https://pyth.network/developers/price-feed-ids
    PythStructs.Price memory price = pyth.getPriceNoOlderThan(feed.id, feed.age);
    // TODO: Do we care about the other stuff?
    tokenPairPrice[tokenIn][tokenOut] = _transformPriceTo18Decimals(price.price, price.expo);
  }

  function withdraw() external onlyOwner {
    // Withdraw the balance of the contract

    //to acquire the balance of this address
    uint256 balance = address(this).balance;
    //false or true
    require(balance > 0, "No balance to withdraw");

    //Emit event
    emit Withdraw(owner(), balance);

    //implement the withdraw
    payable(owner()).transfer(address(this).balance);
  }

  function addFeed(
    PythPriceFeed calldata priceFeed,
    IERC20Metadata tokenIn,
    IERC20Metadata tokenOut
  ) external onlyOwner {
    // confirm the validaty
    require(priceFeed.id != bytes32(0), "this feed is not work");

    tokenPairPriceFeed[tokenIn][tokenOut] = PythPriceFeed({ id: priceFeed.id, age: priceFeed.age });

    // Emit event
    emit FeedAdded(address(tokenIn), address(tokenOut), priceFeed.id);
  }

  // TODO: Check
  function _transformPriceTo18Decimals(int64 price, int32 expo) private pure returns (int256) {
    // Convert expo + 18 to uint256 for exponentiation
    int256 factor = int256(10) ** uint256(int256(expo + 18));

    // Multiply the price by the factor, ensuring the result is in int256
    return int256(price) * factor;
  }

  // Receive ether to pay to the pyth oracle
  receive() external payable {}
}

contracts/P2PSwapRouter.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.0;

import { WadRayMath } from "./dependencies/WadRayMath.sol";
import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";
import { SafeCast } from "@openzeppelin/contracts/utils/math/SafeCast.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { ISwapRouter } from "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import { IERC20Metadata } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

/**
 * @title P2PSwapRouter
 * @notice Contract following the interface of ISwapRouter that executes single swaps from authorized contracts
 *         at configured prices, on behalf of an account
 */
contract P2PSwapRouter is ISwapRouter, AccessControl {
  using SafeERC20 for IERC20Metadata;
  using WadRayMath for uint256;
  using SafeCast for uint256;

  bytes32 public constant SWAP_ROLE = keccak256("SWAP_ROLE");
  bytes32 public constant PRICER_ROLE = keccak256("PRICER_ROLE");
  bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

  error NotImplemented();

  event PriceUpdated(address tokenIn, address tokenOut, uint256 price);
  event OnBehalfOfChanged(address indexed onBehalfOf);

  mapping(address => mapping(address => uint256)) private _prices;
  address internal _onBehalfOf;

  constructor(address onBehalfOf, address admin) {
    _grantRole(DEFAULT_ADMIN_ROLE, admin);
    _setOnBehalfOf(onBehalfOf);
  }

  function _setOnBehalfOf(address onBehalfOf) internal {
    _onBehalfOf = onBehalfOf;
    emit OnBehalfOfChanged(onBehalfOf);
  }

  function getOnBehalfOf() external view returns (address) {
    return _onBehalfOf;
  }

  function _toWadFactor(address token) internal view returns (uint256) {
    return (10 ** (18 - IERC20Metadata(token).decimals()));
  }

  /**
   * @inheritdoc ISwapRouter
   */
  function exactInputSingle(
    ExactInputSingleParams calldata params
  ) external payable onlyRole(SWAP_ROLE) returns (uint256 amountOut) {
    require(params.recipient != address(0), "Recipient cannot be zero address");
    require(params.deadline >= block.timestamp, "Deadline in the past");
    require(params.amountIn > 0, "amountIn cannot be zero");

    uint256 amountOutInWad = (params.amountIn * _toWadFactor(params.tokenIn)).wadDiv(
      _prices[params.tokenIn][params.tokenOut]
    );
    amountOut = amountOutInWad / _toWadFactor(params.tokenOut);
    require(amountOut >= params.amountOutMinimum, "The output amount is less than the slippage");

    IERC20Metadata(params.tokenIn).safeTransferFrom(msg.sender, _onBehalfOf, params.amountIn);
    IERC20Metadata(params.tokenOut).safeTransferFrom(_onBehalfOf, params.recipient, amountOut);
  }

  /**
   * @inheritdoc ISwapRouter
   */
  function exactOutputSingle(
    ExactOutputSingleParams calldata params
  ) external payable onlyRole(SWAP_ROLE) returns (uint256 amountIn) {
    require(params.recipient != address(0), "Recipient cannot be zero address");
    require(params.deadline >= block.timestamp, "Deadline in the past");
    require(params.amountOut > 0, "AmountOut cannot be zero");

    uint256 amountInWad = (params.amountOut * _toWadFactor(params.tokenOut)).wadMul(
      _prices[params.tokenIn][params.tokenOut]
    );
    amountIn = amountInWad / _toWadFactor(params.tokenIn);

    require(amountIn <= params.amountInMaximum, "The input amount exceeds the slippage");

    IERC20Metadata(params.tokenIn).safeTransferFrom(msg.sender, _onBehalfOf, amountIn);
    IERC20Metadata(params.tokenOut).safeTransferFrom(_onBehalfOf, params.recipient, params.amountOut);
  }

  function setCurrentPrice(address tokenIn, address tokenOut, uint256 price_) external onlyRole(PRICER_ROLE) {
    require(tokenIn != address(0), "P2PSwapRouter: tokenIn cannot be the zero address");
    require(tokenOut != address(0), "P2PSwapRouter: tokenOut cannot be the zero address");
    _prices[tokenIn][tokenOut] = price_;
    emit PriceUpdated(tokenIn, tokenOut, price_);
  }

  function getCurrentPrice(address tokenIn, address tokenOut) external view returns (uint256) {
    return _prices[tokenIn][tokenOut];
  }

  function setOnBehalfOf(address onBehalfOf) external onlyRole(ADMIN_ROLE) {
    _setOnBehalfOf(onBehalfOf);
  }

  /**
   * @inheritdoc ISwapRouter
   * @notice This function is not implemented
   */
  function exactOutput(ExactOutputParams calldata) external payable returns (uint256) {
    revert NotImplemented();
  }

  /**
   * @inheritdoc ISwapRouter
   * @notice This function is not implemented
   */
  function exactInput(ExactInputParams calldata) external payable returns (uint256) {
    revert NotImplemented();
  }

  /**
   * @notice This function is not implemented
   */
  function uniswapV3SwapCallback(int256, int256, bytes calldata) external pure {
    revert NotImplemented();
  }
}

contracts/RiskHub.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.0;

import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC20Metadata } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import { IBridge } from "./interfaces/IBridge.sol";
import { IRiskHub } from "./interfaces/IRiskHub.sol";
import { IBangDEX } from "./interfaces/IBangDEX.sol";

/**
 * @title RiskHub
 * @notice Contract that actually concentrates the risk of all the Bang protocol, receiving notification of
 *         swaps from different chains and refunding them.
 */
contract RiskHub is AccessControl, IRiskHub {
  using SafeERC20 for IERC20Metadata;

  bytes32 public constant DEX_ADMIN_ROLE = keccak256("DEX_ADMIN_ROLE");
  bytes32 public constant DEX_LIQUIDITY_ROLE = keccak256("DEX_LIQUIDITY_ROLE");
  bytes32 public constant WITHDRAW_ROLE = keccak256("WITHDRAW_ROLE");
  bytes32 public constant DEX_MESSENGER_ROLE = keccak256("DEX_MESSENGER_ROLE");
  uint256 public constant WAD = 1e18;

  // Event emitted when a message is sent to another chain.
  event TradeReceivedFromDex(
    uint64 indexed chainId,
    IERC20Metadata indexed tokenIn,
    uint40 timestamp,
    uint256 amountIn,
    uint256 amountOut
  );

  IERC20Metadata public immutable payToken; // USDC or other token that will use to pay for the acquired tokens
  IBridge public bridge;

  struct DEX {
    address bangDex;
    uint32 slotSize;
  }

  // Token chainId => DEX
  mapping(uint64 => DEX) public dexes;

  error NotImplemented();

  constructor(IERC20Metadata payToken_, IBridge bridge_, address admin) {
    payToken = payToken_;
    bridge = bridge_;
    _grantRole(DEFAULT_ADMIN_ROLE, admin);
  }

  function addDex(uint64 chainId, address bangDex, uint32 slotSize) external onlyRole(DEX_ADMIN_ROLE) {
    dexes[chainId].bangDex = bangDex;
    dexes[chainId].slotSize = slotSize;
  }

  function withdraw(uint256 amount, address destination) external onlyRole(WITHDRAW_ROLE) {
    payToken.safeTransfer(destination, amount);
  }

  function deposit(uint256 amount) external {
    payToken.safeTransferFrom(msg.sender, address(this), amount);
  }

  /**
   * Sends money to a DEX to increase its liquidity
   // TODO: Add onlyRole(DEX_LIQUIDITY_ROLE)
   */
  function sendToDex(uint64 chainId, uint256 amount) external {
    address target = address(dexes[chainId].bangDex);
    require(target != address(0), "Dex doesn't exists");
    payToken.approve(address(bridge), amount);
    bridge.transferToken(payToken, chainId, target, amount);
  }

  function withdrawFromDex(uint64 chainId, uint256 amount) external onlyRole(DEX_LIQUIDITY_ROLE) {
    address target = address(dexes[chainId].bangDex);
    require(target != address(0), "Dex doesn't exists");
    bytes memory message = abi.encodeWithSelector(IBangDEX.sendToRiskHub.selector, amount);
    bridge.callCrossChain(chainId, target, message);
  }

  /**
   * Called from a DEX when a trade happens
   */
  function tradeFromDex(
    uint64 chainId,
    uint40 timestamp,
    IERC20Metadata tokenIn,
    uint256 amountIn,
    uint256 amountOut
  ) external onlyRole(DEX_MESSENGER_ROLE) {
    // Refunds the DEX
    address target = address(dexes[chainId].bangDex);
    require(target != address(0), "Dex doesn't exists");
    payToken.approve(address(bridge), amountOut);
    bridge.transferToken(payToken, chainId, target, amountOut);
    emit TradeReceivedFromDex(chainId, tokenIn, timestamp, amountIn, amountOut);
  }
}
